def get_signature():
    """
    Build signature for Zou emails.
    """
    organisation = persons_service.get_organisation()
    return (
        """
<p>Best regards,</p>

<p>%s Team</p>"""
        % organisation["name"]
    )


def generate_html_body(title, message):
    return (
        notification_template_begin
        + title
        + notification_template_end_title
        + message
        + get_signature()
        + notification_template_end
    )
